mapper.select:
select id, sid, uid, name, create_time, msgid, state from stock_order
where 1 = 1

    and id = #{id}


    and sid = #{sid}


    and uid = #{uid}


    and name = #{name}


    and create_time > #{startTime} and create_time $lt; #{endTime}


    and state = #{state}

order by create_time desc
limit #{limit}, #{offset}

select count(1) from stock_order
where 1 = 1

    and id = #{id}


    and sid = #{sid}


    and uid = #{uid}


    and name = #{name}


    and create_time > #{startTime} and create_time $lt; #{endTime}


    and state = #{state}

mapper.update:
update stock_order
set state = #{state}
where id = #{id} and state = #{oldState}
